                                                                                                                                                                                                                                                                                                                                                                                                                                            if ($lastpage < 7 + ($adjacents * 2))  //not enough pages to bother breaking it up
                                                                                                                                                                                                                                                                                                                                                                                                                                            {
                                                                                                                                                                                                                                                                                                                                                                                                                                              for ($counter = 1; $counter <= $lastpage; $counter++) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                if ($counter == $page)
                                                                                                                                                                                                                                                                                                                                                                                                                                                  $pagination .= "<span class=\"current\">$counter</span>";
                                                                                                                                                                                                                                                                                                                                                                                                                                                else
                                                                                                                                                                                                                                                                                                                                                                                                                                                  $pagination .= "<a href=\"$targetpage&page=$counter\">$counter</a>";
