                                                                                                                                                                                                                                                                                                                                                                                                                                                $pagination .= "<a href=\"$targetpage&page=$lpm1\">$lpm1</a>";
                                                                                                                                                                                                                                                                                                                                                                                                                                                $pagination .= "<a href=\"$targetpage&page=$lastpage\">$lastpage</a>";
