                                                                                                                                                                                                                                                                                                                                                                                                                                          /* Get data. */
                                                                                                                                                                                                                                                                                                                                                                                                                                          $sql = "select *,DATE_FORMAT(`startDate`,'%d/%m/%Y %H:%i:%s') AS startDatem,DATE_FORMAT(`EndDate`,'%d/%m/%Y %H:%i:%s') AS EndDatem FROM " . $prefix . "grantcalls order by grantID desc LIMIT $start, $limitm";
